Code
filteredMinerals = minerals.filter(mineral =&gt; {
  // Check hardness with ±1 tolerance
  const hardnessMatch = (mineral.hardness[0] &lt;= hardnessMin + 1) && (mineral.hardness[1] &gt;= hardnessMin - 1);

  // Check color match (if any colors selected)
  const colorMatch = selectedColors.length === 0 || 
    selectedColors.some(color =&gt; mineral.colors.includes(color));

  // Check luster match - more flexible matching
  const lusterMatch = selectedLuster === "any" || mineral.luster.toLowerCase().includes(selectedLuster.toLowerCase());

  // Check streak match - more flexible matching
  const streakMatch = selectedStreak === "any" || mineral.streak.toLowerCase().includes(selectedStreak.toLowerCase());

  return hardnessMatch && colorMatch && lusterMatch && streakMatch;
})









